import React, { useState, useEffect } from 'react';
import { Text, View, TouchableOpacity } from 'react-native';
import { DataTable } from 'react-native-paper';
import API from './api';


import Attendance from './components/Attendance';
import Classroom from './components/Classroom';
import Section from './components/Section';

export default function Take_Attendance() {

    const [state, updateState] = useState({
        students: [],
        attendance: {},
        showSubmit: false,
    });

    const [section, setSection] = useState("");
    const [classroom, setClassroom] = useState("");

    var today = new Date();
    var year = today.getFullYear();
    var month = (today.getMonth() + 1);
    var day = today.getDate();

    if (month < 10) month = "0" + month;
    if (day < 10) day = "0" + day;

    var date = year + '-' + month + '-' + day;

    const filter = {
        section: section,
        classroom: classroom,
    };

    const fetchdata = async () => {

        setState({ attendance: {} });

        await API.post(`stu-sec-class`)
            .then(res => {
                const result = res.data.data;
                const data = result.filter(s => s.section_id == section);
                if (data) {
                    data.map(s => {
                        const attendance = state.attendance;
                        attendance[s.id] = "Present";
                        setState({ attendance: attendance });
                    });
                }
                setState({ students: data });
            });

        await API.get(`attendance`)
            .then(res => {
                const result = res.data.data;
                const update = result.filter(a =>
                    (a.created_at.slice(0, 10) == date && a.section_id == section)
                );
                if (update && update.length) {
                    update.map(s => {
                        const attendance = state.attendance;
                        attendance[s.student_id] = s.description;
                        setState({ attendance: attendance });
                    });
                }
            });

        if (classroom == "") setSection("");
        checkSubmit();
    }

    const checkSubmit = () => {
        (section != "") ? setState({ showSubmit: true }) : setState({ showSubmit: false });
    }

    const setState = (nextState) => {
        updateState(prevState => ({
            ...prevState,
            ...nextState
        }));
    }

    const handleChange = e => {
        let { name, value } = e.target;
        setState({ [name]: value });
    }

    const changeDescription = (key, val) => {
        const attendance = state.attendance;
        attendance[key] = val;
        setState({ attendance: attendance });
    }

    const submitAttendance = async () => {

        const updateFunction = async (update) => {
            for (let i = 0; i < state.students.length; i++) {
                let student = state.students[i];
                let key = student.id;
                let reqBody = {
                    description: state.attendance[key],
                    section_id: section,
                    student_id: key
                }
                let att = update.find(a => a.student_id == key);
                let id = att.id;

                await API.put(`attendance/${id}`, reqBody);
            }
        }

        const createFunction = async () => {
            state.students.map(async (student) => {
                let key = student.id;
                let reqBody = {
                    description: state.attendance[key],
                    section_id: section,
                    student_id: key
                }
                await API.post(`attendance`, reqBody);
            });
        }

        await API.get(`attendance`)
            .then(res => {
                const result = res.data.data;
                const update = result.filter(a =>
                    (a.created_at.slice(0, 10) == date && a.section_id == section)
                );
                if (update && update.length) {
                    updateFunction(update);
                } else {
                    createFunction();
                }
            });
    }

    useEffect(() => {
        fetchdata();
    }, [JSON.stringify(filter)]);


    return (
        <View>

            <Text></Text>
            <Text></Text>

            <View>
                <View className="helloclass">
                    <Classroom
                        id={classroom}
                        onValueChange={setClassroom}
                    />
                    <Section
                        id={classroom}
                        onValueChange={setSection}
                        selectedValue={section}
                    />
                </View>

                <TouchableOpacity
                    onPress={submitAttendance}
                    style={{ display: state.showSubmit ? 'block' : 'none' }}>
                    <Text>Submit</Text>
                </TouchableOpacity>
            </View>

            <View>
                <DataTable>
                    <DataTable.Header>
                        <DataTable.Title>ID</DataTable.Title>
                        <DataTable.Title>Name</DataTable.Title>
                        <DataTable.Title>Class</DataTable.Title>
                        <DataTable.Title>Attendance</DataTable.Title>
                    </DataTable.Header>

                    {state.students.map(student => (
                        <DataTable.Row>
                            <DataTable.Cell>{student.id}</DataTable.Cell>
                            <DataTable.Cell>{student.fname} {student.lname}</DataTable.Cell>
                            <DataTable.Cell>{student.classroom_name} {student.section_name}</DataTable.Cell>
                            <DataTable.Cell>
                                <Attendance
                                    name="attendance"
                                    // value={state.attendance.key}
                                    id={state.attendance[student.id]}
                                    className="filterClass"
                                    onChange={e => { return changeDescription(student.id, e.target.value) }}
                                />
                            </DataTable.Cell>
                        </DataTable.Row>
                    ))}
                </DataTable>
            </View>
        </View>
    );
}
